sourcesContent:
import React from "react";
import Footer from "../Footer";
import Header from "../header";

import service10 from "../../assets/images/resource/service-10.png";
import design1 from "../../assets/images/resource/design-1.png";
import design2 from "../../assets/images/resource/design-2.png";
import { Link } from "react-router-dom";

export default function ShopifyDevelopmentServices() {
  return (
    <>
      <div class="page-wrapper">
        <Header />
        <section class="top-container banner-section-five">
          <div class="auto-container">
            <div class="row clearfix">
              <div class="content-column col-lg-6 col-md-12 col-sm-12">
                <div class="inner-column">
                  <p
                    style={{
                      position: "relative",
                      fontStyle: "normal",
                      fontWeight: "400",
                      fontSize: "20px",
                      paddingBottom: "5px",
                      marginBottom: "20px",
                      color: "#212529",
                      marginTop: "0",
                    }}
                  >
                    <span
                      style={{
                        content: "",
                        width: "35px",
                        height: "2px",
                        position: "absolute",
                        bottom: "-4px",
                        left: 0,
                        background: "#2569e6",
                        WebkitTransform: "translateY(-50%)",
                        transform: "translateY(-50%)",
                      }}
                    />
                    Shopify Development Services
                  </p>
                  <h2>We develop quality Shopify Solutions</h2>
                  <div class="text">
                    Hire the Top Shopify Development Company in USA to bring
                    your online store to life
                  </div>
                  <a
                    href="#"
                    style={{ textDecoration: "none" }}
                    class="btn-style-one theme-btn"
                  >
                    Get a Proposal
                  </a>
                </div>
              </div>
              <div class="image-column col-lg-6 col-md-12 col-sm-12">
                <div class="inner-column">
                  <div class="image">
                    <img src={service10} alt="" />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>

        <section class="service-section">
          <div class="container">
            {" "}
            <div class="offer-title">
              <h2 class="home-heading-two">Our Shopify Services</h2>{" "}
            </div>{" "}
            <div class="offer-cards">
              {" "}
              <div class="card">
                {" "}
                <img
                  alt="Settings"
                  nitro-lazy-src="https://cdn-ddddj.nitrocdn.com/NSvsNMzbKkQslXvZnhobiTmmCkBXBUPd/assets/static/optimized/rev-9e570d1/wp-content/themes/tkxel/assets/images/shopify-settings.svg"
                  class=" lazyloaded"
                  nitro-lazy-empty=""
                  id="NTIyOjExOQ==-1"
                  src="https://cdn-ddddj.nitrocdn.com/NSvsNMzbKkQslXvZnhobiTmmCkBXBUPd/assets/static/optimized/rev-9e570d1/wp-content/themes/tkxel/assets/images/shopify-settings.svg"
                />
                <h3>Shopify Store Setup Services</h3>{" "}
                <p>
                  Get your online storefront up and running quickly by
                  leveraging our Shopify theme libraries. We provide Shopify
                  store setup services to help you launch your Shopify website
                  quickly.
                </p>{" "}
              </div>{" "}
              <div class="card">
                {" "}
                <img
                  alt="Template Change"
                  nitro-lazy-src="https://cdn-ddddj.nitrocdn.com/NSvsNMzbKkQslXvZnhobiTmmCkBXBUPd/assets/static/optimized/rev-9e570d1/wp-content/themes/tkxel/assets/images/shopify-template-change.svg"
                  class=" lazyloaded"
                  nitro-lazy-empty=""
                  id="NTI4OjEzMw==-1"
                  src="https://cdn-ddddj.nitrocdn.com/NSvsNMzbKkQslXvZnhobiTmmCkBXBUPd/assets/static/optimized/rev-9e570d1/wp-content/themes/tkxel/assets/images/shopify-template-change.svg"
                />
                <h3>Themes &amp; Customization Services</h3>{" "}
                <p>
                  Make your Shopify store stand out from the crowd with a high
                  quality user experience. Tkxel has expert Shopify designers to
                  ensure that your Shopify store is visually appealing on mobile
                  and web.{" "}
                </p>{" "}
              </div>{" "}
              <div class="card">
                {" "}
                <img
                  alt="Integration"
                  nitro-lazy-src="https://cdn-ddddj.nitrocdn.com/NSvsNMzbKkQslXvZnhobiTmmCkBXBUPd/assets/static/optimized/rev-9e570d1/wp-content/themes/tkxel/assets/images/shopify-integration--icon.svg"
                  class=" lazyloaded"
                  nitro-lazy-empty=""
                  id="NTM1OjEzMQ==-1"
                  src="https://cdn-ddddj.nitrocdn.com/NSvsNMzbKkQslXvZnhobiTmmCkBXBUPd/assets/static/optimized/rev-9e570d1/wp-content/themes/tkxel/assets/images/shopify-integration--icon.svg"
                />
                <h3>Shopify Integration Services</h3>{" "}
                <p>
                  Integrate third party components and platforms with Shopify to
                  provide a seamless experience. Our Shopify integration
                  services help make your ecommerce software versatile and
                  adaptable.
                </p>{" "}
              </div>{" "}
              <div class="card">
                {" "}
                <img
                  alt="Backend"
                  nitro-lazy-src="https://cdn-ddddj.nitrocdn.com/NSvsNMzbKkQslXvZnhobiTmmCkBXBUPd/assets/static/optimized/rev-9e570d1/wp-content/themes/tkxel/assets/images/shopify-backend.svg"
                  class=" lazyloaded"
                  nitro-lazy-empty=""
                  id="NTQxOjExNw==-1"
                  src="https://cdn-ddddj.nitrocdn.com/NSvsNMzbKkQslXvZnhobiTmmCkBXBUPd/assets/static/optimized/rev-9e570d1/wp-content/themes/tkxel/assets/images/shopify-backend.svg"
                />
                <h3>Custom Development Services</h3>{" "}
                <p>
